SELECT LastName || ', ' || FirstName AS Name, InvoiceId AS 'Invoice #', CustomerId, InvoiceDate, BillingAddress, BillingCity, BillingState, BillingCountry, BillingPostalCode, Total FROM (SELECT Invoice.*, InvoiceId, Customer.CustomerId, Customer.SupportRepId FROM Invoice JOIN Customer On Customer.CustomerId == Invoice.CustomerId) JOIN Employee ON SupportRepId == Employee.EmployeeId ORDER BY Name
