SELECT LastName || ', ' || FirstName AS 'Sales Rep Name', InvoiceId AS 'Invoice #', CustomerName AS 'Customer Name', BillingCountry, Total FROM (SELECT Invoice.*, Customer.LastName || ', ' || Customer.FirstName AS 'CustomerName', Customer.CustomerId, Customer.SupportRepId FROM Invoice JOIN Customer On Customer.CustomerId == Invoice.CustomerId) JOIN Employee ON SupportRepId == Employee.EmployeeId ORDER BY InvoiceId
